  // Get references to the button and additional info elements
  const toggleButton = document.getElementById('toggle-button');
  const additionalInfo = document.getElementById('additional-info');

  // Add click event listener to the button
  toggleButton.addEventListener('click', function() {
    // Toggle the visibility of the additional info section
    if (additionalInfo.style.display === 'none') {
      additionalInfo.style.display = 'block';
    } else {
      additionalInfo.style.display = 'none';
    }
  });




  // Get references to the necessary elements
  const sliderImages = document.getElementById('slider-images');
  const imageItems = sliderImages.getElementsByTagName('li');
  let currentIndex = 0;

  // Function to show the current image and hide the others
  function showCurrentImage() {
    for (let i = 0; i < imageItems.length; i++) {
      if (i === currentIndex) {
        imageItems[i].style.display = 'block';
      } else {
        imageItems[i].style.display = 'none';
      }
    }
  }

  // Function to switch to the next image
  function nextImage() {
    currentIndex = (currentIndex + 1) % imageItems.length;
    showCurrentImage();
  }

  // Function to start the automatic image cycling
  function startImageSlider() {
    setInterval(nextImage, 2000); // Change the time interval (in milliseconds) as desired
  }

  // Call the startImageSlider function to begin automatic image cycling
  startImageSlider();




  const contactForm = document.getElementById('contact-form');
  const successMessage = document.getElementById('success-message');

  function handleSubmit(event) {
    event.preventDefault();

    const name = document.getElementById('name').value;
    const email = document.getElementById('email').value;
    const phone = document.getElementById('phone').value;
    const message = document.getElementById('message').value;

    if (name.trim() === '' || email.trim() === '' || phone.trim() === '' || message.trim() === '') {
      alert('Please fill in all fields');
      return;
    }

    const emailRegex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if (!emailRegex.test(email)) {
      alert('Please enter a valid email address');
      return;
    }

    const phoneRegex = /^\d{10}$/;
    if (!phoneRegex.test(phone)) {
      alert('Please enter a valid 10-digit phone number');
      return;
    }

    // Simulating form submission success
    successMessage.textContent = 'Form submitted successfully!';
    contactForm.reset();
  }

  contactForm.addEventListener('submit', handleSubmit);





  // Get references to the navigation links
  const navLinks = document.querySelectorAll('nav a');

  // Add click event listener to each navigation link
  navLinks.forEach(function(link) {
    link.addEventListener('click', function(event) {
      event.preventDefault(); // Prevent the default link behavior

      // Get the target section's ID from the href attribute
      const targetId = link.getAttribute('href');

      // Use smooth scrolling to scroll to the target section
      document.querySelector(targetId).scrollIntoView({
        behavior: 'smooth',
        block: 'start'
      });
    });
  });



  const overlayElements = document.querySelectorAll('.overlay');

overlayElements.forEach((overlay) => {
  overlay.addEventListener('click', showPopup);
});

function showPopup(event) {
  const popupContent = event.currentTarget.querySelector('.overlay-content');
  
  // Create a new div element for the popup
  const popup = document.createElement('div');
  popup.classList.add('popup');
  popup.innerHTML = popupContent.innerHTML;
  
  // Append the popup to the body
  document.body.appendChild(popup);
  
  // Add a click event listener to close the popup when clicked outside
  document.addEventListener('click', closePopupOutside);
}

function closePopupOutside(event) {
  const popup = document.querySelector('.popup');
  
  if (event.target !== popup && !popup.contains(event.target)) {
    popup.remove();
    document.removeEventListener('click', closePopupOutside);
  }
}




const portfolioItems = document.querySelectorAll('.portfolio-item');

window.addEventListener('scroll', checkScroll);

function checkScroll() {
  const triggerBottom = window.innerHeight * 0.8;

  portfolioItems.forEach((item) => {
    const itemTop = item.getBoundingClientRect().top;

    if (itemTop < triggerBottom) {
      item.classList.add('show');
    }
  });
}


const projects = document.querySelectorAll('.project');

projects.forEach((project) => {
  project.addEventListener('mouseover', () => {
    project.classList.add('hovered');
  });

  project.addEventListener('mouseout', () => {
    project.classList.remove('hovered');
  });
});
